mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail} , manufacture_day = #{manuDate}, price = #{price}, image_file = #{fileName},
WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT p.prod_no, p.prod_name , p.price , p.reg_date, TRIM(t.tran_status_code) tran_code
										FROM product p, transaction t
										WHERE p.prod_no = t.prod_no(+)
										
											
									 				AND p.prod_no = #{search.searchKeyword}
											
											
									 				AND p.prod_name LIKE #{search.searchKeyword}
											
											
									 				AND p.price = #{search.searchKeyword} AND #{search.priceRange}
											
										
										
										
											AND p.category_no = #{categoryNo}
										
										
										
											
												ORDER BY p.reg_date
											
											
												ORDER BY p.price
											
											
												ORDER BY p.price DESC
											
										
										
											ORDER BY p.reg_date
										
										 ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT p.prod_no, p.prod_name , p.price , p.reg_date, TRIM(t.tran_status_code) tran_code
										FROM product p, transaction t
										WHERE p.prod_no = t.prod_no(+)
										
											
									 				AND p.prod_no = #{search.searchKeyword}
											
											
									 				AND p.prod_name LIKE #{search.searchKeyword}
											
											
									 				AND p.price = #{search.searchKeyword} AND #{search.priceRange}
											
										
										
										
											AND p.category_no = #{categoryNo}
										
										
										
											
												ORDER BY p.reg_date
											
											
												ORDER BY p.price
											
											
												ORDER BY p.price DESC
											
										
										
											ORDER BY p.reg_date
										) countTable
